import ajax from '../https'

const devIP = 'http://192.168.2.10:8000' // 开发IP地址
const testIP2 = 'http://47.103.94.48:8806' // 绿灯测试IP地址
const testIP3 = 'http://47.103.143.165:8806' // 绿灯测试2IP地址
const product = 'http://47.88.228.146:8806' // 生产环境IP地址
const spare1 = 'http://45.32.119.58:8066' // 备用环境1IP地址
const spare2 = 'http://157.230.241.99:8066' // 备用环境2IP地址
const productRealmIP = 'http://sign.mars99999.cloud' // 生产环境（域名）IP地址

export function login(params) {
  // 登陆注册
  return ajax({
    url: spare2 + '/web/sys/pblin.do',
    method: 'post',
    data: params
  })
}

export function getSignData(params) {
  // 获取签名数据
  return ajax({
    url: spare2 + '/web/sgn/pbreq.do',
    method: 'post',
    data: params
  })
}

export function sendSignData(params) {
  // 发起签名
  return ajax({
    url: spare2 + '/web/sgn/pbsnt.do',
    method: 'post',
    data: params
  })
}
